function startTime() {
    var today = new Date();
    var hr = today.getHours();
    var min = today.getMinutes();
    var sec = today.getSeconds();
    //ap = (hr < 12) ? "<span>AM</span>" : "<span>PM</span>";
    hr = (hr == 0) ? 12 : hr;
    //hr = (hr > 12) ? hr - 12 : hr;
    //Add a zero in front of numbers<10
    hr = checkTime(hr);
    min = checkTime(min);
    sec = checkTime(sec);
    // document.getElementById("clock").innerHTML = `${hr}:${min}:${sec}`;
    document.getElementById("sidebar-clock").querySelectorAll("td")[0].innerHTML = `${hr}`.split("")[0]
    document.getElementById("sidebar-clock").querySelectorAll("td")[1].innerHTML = `${hr}`.split("")[1]
    document.getElementById("sidebar-clock").querySelectorAll("td")[3].innerHTML = `${min}`.split("")[0]
    document.getElementById("sidebar-clock").querySelectorAll("td")[4].innerHTML = `${min}`.split("")[1]
    document.getElementById("sidebar-clock").querySelectorAll("td")[6].innerHTML = `${sec}`.split("")[0]
    document.getElementById("sidebar-clock").querySelectorAll("td")[7].innerHTML = `${sec}`.split("")[1]

    var months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    var days = ['Chủ nhật', 'Thứ 2', 'Thứ 3', 'Thứ 4', 'Thứ 5', 'Thứ 6', 'Thứ 7'];
    var curWeekDay = days[today.getDay()];
    var curDay = today.getDate();
    //var curMonth = months[today.getMonth()];
    var curMonth = `Tháng ${today.getMonth() + 1}`
    var curYear = today.getFullYear();
    var date = `${curWeekDay}, ${curDay} ${curMonth}`;
    document.getElementById("date").innerHTML = date;
}

function checkTime(i) {
    if (i < 10) {
        i = "0" + i;
    }
    return i;
}
if(document.getElementById("sidebar-clock")){

    startTime()
    setInterval(function () {
        startTime()
    }, 1000);
    
}